/* eslint-disable */
<template>
  <div>
    <header :class="{'bg-transparent': !isLogoHidden}" class="bg-black fixed top-0 left-0 right-0 z-10 p-4 flex justify-between items-center z-20" style="transition:all .5s ease-in-out">
            <button @click="navigateToSecondView" class="flex items-center menu-bar">
              <span class="grid grid-rows-3 gap-2.5 lg:gap-3">
                <span class="block w-10 h-0.5"></span>
                <span class="block w-10 h-0.5"></span>
                <span class="block w-10 h-0.5"></span>
              </span>
              <span class="menu-text text-white ml-2 hidden md:block">Menu</span>
            </button>
            <img src="../../public/LOGO_BLANC.svg" all="LOGO COMPLET LE TEM" id="logo1" class="max-h-10 md:hidden flex justify-center items-center">
      <img src="../../public/LOGO_BLANC_LETEM.svg" alt="LOGO LE TEM" id="logo2"
        class="h-full max-h-10 lg:hidden" v-if="!isLogoHidden" :class="{'oklm' : !isLogoHidden}">
      <div class="flex">
        <button>
          <span class="text-xl text-white underline hover:text-yellow-200 mx-1 md:mx-3 lg:mx-5" style="transition:all .5s ease-in-out">Réserver</span>
        </button>
        <button>
          <span @click="OpenPageOffrir" class="text-xl text-white underline hover:text-yellow-200 mx-1 md:mx-3 lg:mx-5"  style="transition:all .5s ease-in-out">Offrir</span>
        </button>
        <button>
          <span class="text-xl text-white underline hover:text-yellow-200 mx-1 md:mx-3 lg:mx-5"  style="transition:all .5s ease-in-out">Contact</span>
        </button>
      </div>
      <img src="../../public/LOGO_BLANC.svg" all="LOGO COMPLET LE TEM" id="logo3"
        class="hidden h-48 absolute left-1/2 transform -translate-x-1/2 z-2"
        :class="{ 'lg:block': !isLogoHidden}"  style="bottom: -155px; transition:all .5s ease-in-out;">
        </header>
  </div>
</template>

<style>

.block {
  background-color: white;
}

.menu-bar:hover .block {
  background-color: #F2D024;
  transition:all .5s ease-in-out;
}

.menu-bar:hover .menu-text {
  color: #F2D024;
  transition:all .5s ease-in-out;
}

</style>

<script>
export default {
  data() {
    return {
      isLogoHidden: false,
    };
  },
  mounted() {
    window.addEventListener('scroll', this.handleScroll);
  },
  unmounted() {
    window.removeEventListener('scroll', this.handleScroll);
  },
  methods: {
    handleScroll() {
      if (window.scrollY > 500) {
        this.isLogoHidden = true; // Cacher le logo lorsque le défilement dépasse 500 pixels
      } else {
        this.isLogoHidden = false; // Afficher le logo lorsque le défilement est inférieur à 500 pixels
      }
    },
    navigateToSecondView() {
      this.$router.push('/letem54/menu');
    },
    OpenPageOffrir() {
      window.open('http://localhost:8080/letem54/offrir', '_blank');
    },
  },
}
</script>
