<template>
  <div style="position: relative;">
    <header class="fixed top-0 left-0 right-0 z-10 bg-black p-4 flex flex-row justify-center" :class="{ 'lg:bg-transparent': !isLogoHidden}" style="transition:all .5s ease-in-out">
      <button @click="navigateToSecondView" class="flex items-center">
        <span class="grid grid-rows-3 gap-2.5 lg:gap-3" style="transition:all .5s ease-in-out">
          <span class="block w-10 h-0.5 bg-white"></span>
          <span class="block w-10 h-0.5 bg-white"></span>
          <span class="block w-10 h-0.5 bg-white"></span>
        </span>
        <span class="md:text-white md:ml-2">Menu</span>
      </button>
      <!-- <img src="../../public/LOGO_BLANC.svg" all="LOGO COMPLET LE TEM" id="logo1" class="h-full max-h-10 md:hidden">
      <img src="../../public/LOGO_BLANC_LETEM.svg" alt="LOGO LE TEM" id="logo2"
        class="h-full max-h-10 lg:hidden hidden md:block"> -->
      <div class="flex ">
        <button>
          <span class="text-xl mx-4 text-white underline">Réserver</span>
        </button>
        <button>
          <span class="text-xl mx-4 text-white underline">Offrir</span>
        </button>
      </div>
      <!-- <img src="../../public/LOGO_BLANC.svg" all="LOGO COMPLET LE TEM" id="logo3"
        class="hidden h-48 absolute left-1/2 transform -translate-x-1/2 z-2"
        :class="{ 'lg:block': !isLogoHidden}"  style="bottom: -155px; transition:all .5s ease-in-out;"> -->
    </header>
  </div>
</template>

<script>
export default {
  data() {
    return {
      isLogoHidden: false,
    };
  },
  mounted() {
    window.addEventListener('scroll', this.handleScroll);
  },
  unmounted() {
    window.removeEventListener('scroll', this.handleScroll);
  },
  methods: {
    handleScroll() {
      if (window.scrollY > 500) {
        this.isLogoHidden = true; // Cacher le logo lorsque le défilement dépasse 500 pixels
      } else {
        this.isLogoHidden = false; // Afficher le logo lorsque le défilement est inférieur à 500 pixels
      }
    },
    navigateToSecondView() {
      this.$router.push('/letem54/menu');
    }
  }
}
</script>
